sys_script_include.script:
var ServiceOfferingChecker = Class.create();
ServiceOfferingChecker.prototype = {
    initialize: function() {},

    companyHasAVS: function(user) {
		var answer = false;
		var company = user.getCompanyID();
        var soGR = new GlideRecord('service_subscribe_company');
        soGR.addEncodedQuery('service_offering=77aa056147e8d110be5a9db6926d4336^core_company='+company);
        soGR.query();
        while (soGR.next()) {
            if (soGR.core_company == company) {
                answer = true;
            }
        }
		return answer;

    },

	companyHasSCCMS: function(user) {
		var answer = false;
		var company = user.getCompanyID();
        var soGR = new GlideRecord('service_subscribe_company');
        soGR.addEncodedQuery('service_offering=6122228747209910be5a9db6926d4385^core_company='+company);
        soGR.query();
        while (soGR.next()) {
            if (soGR.core_company == company) {
                answer = true;
            }
        }
		return answer;

    },

	companyHasDCS: function(user) {
		var answer = false;
		var company = user.getCompanyID();
        var soGR = new GlideRecord('service_subscribe_company');
        soGR.addEncodedQuery('service_offering=6122228747209910be5a9db6926d4385^core_company='+company);
        soGR.query();
        while (soGR.next()) {
            if (soGR.core_company == company) {
                answer = true;
            }
        }
		return answer;

    },




    type: 'ServiceOfferingChecker'
};
